const Posts = ({ posts, token }) => {
  const handleDelete = async () => {
    const respObj = await callApi({
      url: `/posts/POST_ID`,
    });
    console.log("respObj: ", respObj);
  };
  // const [postsx, setPosts] = useState([]);
  // const handleDelete = async (postIdToDelete) => {
