  //   if (data) {
  //     const newPosts = posts.filter((post) => post._id !== postIdToDelete);
  //     setPosts(newPosts);
  //   }
  // };
  return (
    <>
      <h3>List of Posts</h3>
      {posts.map((post) => (
        /* console.log(post)*/
        <div key={post._id}>
          <h1>{post.title}</h1>
          <p>{post.description}</p>
          <p>{post.price}</p>
          <p>{post.location}</p>
          {/* {token && (
            <button
              type="button"
              className="btn btn-outline-primary"
              onClick={() => handleEdit(post._id)}
            >
              Edit
            </button>
          )} */}
          {token && (
            <button
              type="button"
              className="btn btn-outline-secondary"
              onClick={() => handleDelete(post._id)}
            >
              Delete
            </button>
          )}
          {post.isAuthor === false ? (
            <Link
              to="/messages"
              state={{ id: post._id }}
              className="buttonMessage"
            >
              Message
            </Link>
          ) : null}
        </div>
      ))}
    </>
  );
};

export default Posts;
