import React, {useState} from 'react';
import { useParams } from 'react-router';


const cohortName = '2108-ECE-RM-WEB-PT';
const APIURL = `https://strangers-things.herokuapp.com/api/${cohortName}`;




const Login = ({setToken}) =>{
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');


    return <>

    <h1>Login</h1>
    {/* // we need to send a fetch request, so we can get the token
          // in order to get a token, the server wants... username and password */}

console.log(`${APIURL}/users/${params.method}`)
    <form onSubmit={async(event) =>{
        event.preventDefault();
        const response = await fetch(`${APIURL}/users/${params.method}`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                user:{
                    username: 'brian',
                    password: 'walker'
                }
            })
        })
        const responseObj = await response.json();
        setToken(responseObj.data.token)
    }}>
        <input type="text" placeholder="username" value={username} 
        onChange={(event) => setUsername(event.target.value)}></input>
        <hr></hr>

        <input type= "password" placeholder = "password" value={password} 
        onChange ={(event) => setPassword(event.target.value)}></input>
        <hr></hr>

        <button type ="submit" >Submit</button>
    </form>
    </>
}

export default Login;
