    return <>

    <h1>Login/Register</h1>
    {/* // we need to send a fetch request, so we can get the token
          // in order to get a token, the server wants... username and password 
          //When I hit submit it is supposed the username and password from the API. 

          */}


    <form onSubmit={async(event) =>{
        event.preventDefault();
        const URL = (`${APIURL}/users/login`)
        console.log(URL)
       try{
        const response = await fetch(URL, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                // 'Authorization': 'Bearer ${token}'

            },
            body: JSON.stringify({
                user:{
                    username,
                    password
                }
            })
        })
        const responseObj = await response.json();
        return responseObj


       } catch (error){
           console.error(error)
       }
    //    console.log(responseObj.data.token)
       console.log(responseObj)
        // if(responseObj.data.token) setToken(responseObj.data.token)

    }}>
        <input type="text" placeholder="username" value={username} 
        onChange={(event) => setUsername(event.target.value)}></input>
        <hr></hr>

        <input type= "text" placeholder = "password" value={password} 
        onChange ={(event) => setPassword(event.target.value)}></input>
        <hr></hr>
        <button type="submit" disabled={!password || !username}>Submit</button>      

    </form>
    </>
}

export default Login;
