import React, {useState} from 'react';

const Create = (posts, setPosts) => {
    const [title, setTitle] = useState([]);
    const [body, setBody] = useState([]);

    const handleSubmit = async (event) =>{
        event.preventDefault();
        const response = await fetch('https://strangers-things.herokuapp.com/api/2108-ECE-RM-WEB-PT/posts', {
            method: 'POST',
            headers: {
                'Content-type': 'Application/json',
            },
            body: JSON.stringify({
                title,
                body,
            })
        });
        const data = await response.json();
        setPosts([data, ...posts]);
        setTitle('');
        setBody('');

    }
    return <>
        <h3>
            Create a Post
        </h3>
        <form onSubmit={handleSubmit}>
        <input type="text" placeholder="title" value={title} onChange=
        {(event) => setTitle(event.target.value)}></input>
        <input type= "text" placeholder = "body" value=
        {body} onChange ={(event) => setBody(event.target.value)}></input>
        <button type ="submit" className ="btn btn-outline-primary">Submit</button>
        </form>
        </>

}

export default Create;
