import React, {useState} from 'react';
import { useParams,useNavigate } from 'react-router';
import { callApi } from '../util/api';


const cohortName = '2108-ECE-RM-WEB-PT';
const APIURL = `https://strangers-things.herokuapp.com/api/${cohortName}`;

const Register = ({setToken}) =>{
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    return <>

{/* Make it so my password and confirm password have to be equal in order for them to register.  */}


<h1>Login/Register</h1>

<form onSubmit={async(event) =>{
        event.preventDefault();
        const response = await fetch(`${APIURL}/users/register`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',

            },
            body: JSON.stringify({
                user:{
                    username,
                    password,
                    confirmPassword
                }
            })
        })
        .catch(console.error);
        const responseObj = await response.json();
        console.log(responseObj)
        setToken(responseObj.data.token)
        console.log(setToken)

    }}>
        <input type="text" placeholder="username" value={username} 
        onChange={(event) => setUsername(event.target.value)}></input>
        <hr></hr>

        <input type= "text" placeholder = "password" value={password} 
        onChange ={(event) => setPassword(event.target.value)}></input>
        <hr></hr>
        <input type= "text" placeholder = "confirm password" value={confirmPassword} 
        onChange ={(event) => setConfirmPassword(event.target.value)}></input>
        <button type ="submit" >Submit</button>
    </form>
    </>
}

export default Register;
