import React, {useEffect, useState} from "react"
import ReactDOM from "react-dom"
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom'
import Login from './components/Login'
import Posts from './components/Posts'
import Register from './components/Register'
import CreatePost from './components/CreatePost'
import Profile from './components/Profile'


const cohortName = '2108-ECE-RM-WEB-PT';
const APIURL = `https://strangers-things.herokuapp.com/api/${cohortName}`;



const App = ({}) => {
const [token, setToken] = useState('')    
const [posts, setPosts] = useState([])
const [user, setUsers] = useState('');

useEffect(() => {
  try{
  const fetchPosts = async() =>{
    const response = await fetch(`${APIURL}/posts`)
    const respObj = await response.json();
    const posts = respObj.data.posts
    if(posts) setPosts(posts);
    // console.log(respObj)
    // setPosts(respObj.data.posts)
  }
 fetchPosts();
} catch (error){
  console.error(error);
}
},[token])

  return<>

    <header>
    <h1> Stranger Things Test</h1>

    <Link to="/">Home</Link>
    <br></br>
    <Link to="/posts"> Posts</Link>
    <br></br>
    <Link to="/account/login">Login</Link>
    <br></br>
    <Link to="/account/register">Register</Link>
    <br></br>
    <Link to="/account/profile">Profile</Link>
    <button className='logOut'>Log Out!</button>
    </header>
    <Routes>
      <Route path= "/account/login" element={<Login setToken={setToken} setUsers={setUsers}/>}/>
      <Route path= "/account/register" element={<Register setToken={setToken} setUsers={setUsers}/>}/>  
      {/* In final version setToken shouldn't be in register, and be able to disable routes possibly if they aren't signed in...or not logged in */}
      {/* <Route path= "/account/:method" element={<Login setToken={setToken} setUser={setUser}/>}/>  */}
      <Route path="/posts" element={<><CreatePost/> <Posts posts={posts} token={token}/></>}/>
      <Route path="/" element={<Profile username ={user.username}/>}/>
    </Routes>

    </>
  }
ReactDOM.render(
  <Router>
  <App />
  </Router>, 

document.getElementById("app"))
