import React, {useState} from 'react';

const Update = ({posts, setPosts, postId, setPostId}) =>{
    const [title, setTitle] = useState([]);
    const [body, setBody] = useState([]);

    const handleSubmit = async (event) => {
        event.preventDefault();
        console.log('title, body: ', title, body);
        console.log('postId: ', postId);
        const response = await fetch(`https://strangers-things.herokuapp.com/api/2108-ECE-RM-WEB-PT/posts/
        ${postId}`,{
            method: 'PATCH',
            headers: {
                'Content-type': 'Application/json',
            },
        body: JSON.stringify({
            title,
            body,
        })
        })
    } 
    return <>
    <h3>
        Update a post
    </h3>
    <form onSubmit={handleSubmit}>
    <input type="text" placeholder="title" value={title} onchange =
    {(event) => setTitle(event.target.value)}></input>
    <input type="text" placeholder="body"
    value={body} onChange={(event) => setBody
    (event.target.value)}></input>
    <button type="submit" className="btn btn-outline-primary">Submit</button>
    </form>
    </>
}


export default Update;
