import React, { useState, useEffect } from "react";
import { callApi } from "../util/api";

const Profile = ({ username, profile, setProfile, setUserid, token }) => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const fetchProfile = async () => {
      const resp = await callApi({
        url: "/users/me",
        token,
      });
      // const resp = await fetch(`${APIURL}/users/me`, {
      //   headers: {
      //     "Content-Type": "application/json",
      //     Authorization: `Bearer ${localStorage.getItem("token")}`,
      //   },
    };
    // const data = await response.json();
    setUserid(data.data._id);
    setProfile(data.data);
    setMessages(data.data.messages);
    fetchProfile();
  });
  [];

  return (
    <>
      <h1>Profile</h1>

      {username ? <div>You are logged in as {username}</div> : ""}

      <h1 className="title">
        Welcome {profile.username ? profile.username : ""}
      </h1>
      <h3 className="title">Messages to Me:</h3>
      {messages.map((item) =>
        profile._id !== item.fromUser._id ? (
          <div className="post" key={item._id}>
            <h2>Title: {item.post.title}</h2>
            <h3>From: {item.fromUser.username}</h3>
            <h4>{item.content}</h4>
          </div>
        ) : null
      )}
    </>
  );
};

export default Profile;

