import React, { useState, useEffect } from "react";
import { callApi } from "../util/api";
import { useLocation } from "react-router-dom";

const cohortName = "2108-ECE-RM-WEB-PT";
const APIURL = `https://strangers-things.herokuapp.com/api/${cohortName}`;

const Messages = ({ posts, postId, token }) => {
  const [message, setMessage] = useState("");
  const location = useLocation();
  const { id } = location.state;
  console.log(id);

  const sendMessage = async (event) => {
    event.preventDefault();
    console.log(`${APIURL}/posts/${id}`);
    const response = await callApi({
      url: `/posts/${id}/messages`,
      method: "POST",
      token,
      body: {
        message: {
          content: message,
        },
      },
    });
    console.log("response: ", response);
    const respPost = await callApi({ url: `/posts/${id}/messages`, token });
    console.log("respPost: ", respPost);
    // console.log(setPosts);
    postId(respPost.data.posts);

    return (
      <>
        <h1>Send a Message: </h1>
        {posts.map((post) =>
          id === post._id ? (
            <div className="post" key={post.id}>
              <h2>{post.title}</h2>
              <h4>{post.price}</h4>
              <div>{post.description}</div>
              <div>{post.location}</div>
              <form
                onSubmit={async (event) => {
                  event.preventDefault();
                  sendMessage(message);
                }}
              >
                <input
                  type="text"
                  placeholder="message"
                  value={message}
                  onChange={(event) => setMessage(event.target.value)}
                ></input>
                <button>Send Message</button>
              </form>
            </div>
          ) : null
        )}
      </>
    );
  };
};
export default Messages;
