import React, { useState } from "react";
import { callApi } from "../util/api";

const cohortName = "2108-ECE-RM-WEB-PT";
const APIURL = `https://strangers-things.herokuapp.com/api/${cohortName}`;

// Include token here in createpost after I have that set up

const CreatePosts = ({ token, setPosts }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [location, setLocation] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log({ title, description, price, location });
    const response = await callApi({
      url: "/posts",
      method: "POST",
      token,
      body: {
        post: {
          title,
          description,
          price,
          location,
        },
      },
    });
    console.log("response: ", response);
    const respPost = await callApi({ url: "/posts", token });
    console.log("respPost: ", respPost);
    console.log(setPosts);
    setPosts(respPost.data.posts);

    // const response = await fetch(`${APIURL}/posts`, {
    //     method: 'POST',
    //     headers: {
    //         'Content-type': 'Application/json',
    //     },
    //     body: JSON.stringify({
    //         title,
    //         body,
    //     })
    // });
    // const data = await response.json();
    // setPosts([data, ...posts]);
    // setTitle('');
    // setBody('');
  };
  return (
    <>
      {token && <h3>Create a Post if logged in!</h3>}
      <form onSubmit={handleSubmit}>
        {token && (
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(event) => setTitle(event.target.value)}
          ></input>
        )}
        {token && (
          <input
            type="text"
            placeholder="Description"
            value={description}
            onChange={(event) => setDescription(event.target.value)}
          ></input>
        )}

        {token && (
          <input
            type="text"
            placeholder="Price"
            value={price}
            onChange={(event) => setPrice(event.target.value)}
          ></input>
        )}

        {token && (
          <input
            type="text"
            placeholder="Location"
            value={location}
            onChange={(event) => setLocation(event.target.value)}
          ></input>
        )}

        {token && (
          <button type="submit" className="btn btn-outline-primary">
            Posts!
          </button>
        )}
      </form>
    </>
  );
};

export default CreatePosts;
