import ReactDOM from "react-dom"
import {
    Create,
    Update
  } from './components';


const App = () => {
    const [posts, setPosts] = useState([]);



    useEffect(() => {
        const fetchPosts = async () => {
        const resp = await fetch ('https://strangers-things.herokuapp.com/api/2108-ECE-RM-WEB-PT/posts');
        const data = await resp.json();
        setPosts(data.data.posts); 
    }
        fetchPosts();

    }, [])

