import ReactDOM from "react-dom"


const App = () => {
    const [posts, setPosts] = useState([]);
    /*console.log('posts: ', posts); */

    const fetchPosts = async () => {
        const resp = await fetch ('https://strangers-things.herokuapp.com/api/2108-ECE-RM-WEB-PT/posts');
        const data = await resp.json();
        setPosts(data.data); 
    }
    useEffect(() => {

        fetchPosts();

    }, [])

  return <>

  <h1>Posts</h1>
  {console.log(posts)}
{
posts.data.posts.map(post => <div key={post._id}>
    <h3>{post.title}</h3>
    <div>{post.body}</div>
</div>)

}
  </>
}

